import React, { useState } from 'react';
import { motion } from 'framer-motion';

// Opções para Cargo/Departamento
const initialCargoOptions = [
  "Aquisição", "Backoffice", "Consultoria", "Diretoria", "Finanças & Compliance",
  "Pessoas & Cultura", "Securitário", "Sucesso do Cliente", "Tecnologia", "+Novo"
];

const categoryOptions = ["Clientes", "Comissões e/ou SplitC", "Basement", "Novo+"];
const priorityOptions = [
  { label: "Baixa (7 dias úteis)", days: 7 },
  { label: "Média (5 dias úteis)", days: 5 },
  { label: "Alta (2 dias úteis)", days: 2 },
  { label: "Urgente (1 dia útil)", days: 1 }
];
const statusOptions = ["Aberto", "Em andamento", "Fechado"];
const responsibleOptions = ["Nayla Martins", "Jonathan Kauer"];
const primaryTextColor = "#0E1428";
const overdueBackgroundColor = "#FFCCCC";
const buttonColor = "#FF5E00";

// Função para gerar um ID aleatório de 13 caracteres
function generateTicketId() {
  const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
  return Array.from({ length: 13 }, () => chars[Math.floor(Math.random() * chars.length)]).join("");
}

function calculateDueDate(days) {
  let date = new Date();
  let count = 0;
  while (count < days) {
    date.setDate(date.getDate() + 1);
    if (date.getDay() !== 0 && date.getDay() !== 6) {
      count++;
    }
  }
  return date.toLocaleDateString();
}

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loginUser, setLoginUser] = useState("");
  const [loginAdmin, setLoginAdmin] = useState(false);
  const [loginPassword, setLoginPassword] = useState("");
  const [activeTab, setActiveTab] = useState("abertos");
  const [tickets, setTickets] = useState([]);
  const [showNewTicketForm, setShowNewTicketForm] = useState(false);

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    if (loginAdmin && loginPassword !== "admin123@guiainvestgpt") {
      alert("Senha incorreta para Admin!");
      return;
    }
    setCurrentUser(loginUser);
    setIsAdmin(loginAdmin);
    setLoginUser("");
    setLoginAdmin(false);
    setLoginPassword("");
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setIsAdmin(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4 text-[#0E1428]">
      <img src="/logo.png" alt="FinDesk Logo" className="h-12 mb-4" />
      <motion.h1 className="text-3xl font-bold mb-4">
        FinDesk
      </motion.h1>

      {!currentUser ? (
        <form onSubmit={handleLoginSubmit} className="bg-white shadow p-4 rounded-2xl w-full max-w-md mb-4">
          <h2 className="text-xl font-bold mb-4">Faça seu login</h2>
          <input type="text" placeholder="Nome de Usuário" value={loginUser} onChange={(e) => setLoginUser(e.target.value)} required className="border rounded px-2 py-1 w-full mb-2" />
          <div className="mb-2 flex items-center">
            <input type="checkbox" checked={loginAdmin} onChange={(e) => setLoginAdmin(e.target.checked)} className="mr-2" />
            <span>Entrar como Admin</span>
          </div>
          {loginAdmin && (
            <input type="password" placeholder="Senha" value={loginPassword} onChange={(e) => setLoginPassword(e.target.value)} required className="border rounded px-2 py-1 w-full mt-2" />
          )}
          <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded mt-2">Entrar</button>
        </form>
      ) : (
        <div className="w-full max-w-3xl">
          <div className="flex justify-between w-full mb-4">
            <p className="text-lg">Bem-vindo, <span className="font-bold">{currentUser}</span> {isAdmin && <span className="text-red-500">(Admin)</span>}</p>
            <button onClick={handleLogout} className="px-3 py-1 bg-red-500 text-white rounded shadow">Sair</button>
          </div>
          <div className="flex space-x-4 mb-4">
            <button onClick={() => setActiveTab("abertos")} className={`px-4 py-2 rounded ${activeTab === "abertos" ? "bg-blue-600 text-white" : "bg-gray-300"}`}>Chamados Abertos</button>
            <button onClick={() => setActiveTab("concluidos")} className={`px-4 py-2 rounded ${activeTab === "concluidos" ? "bg-blue-600 text-white" : "bg-gray-300"}`}>Chamados Concluídos</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
