import React, { useState } from 'react';
import { motion } from 'framer-motion';

// Opções para Cargo/Departamento
const initialCargoOptions = [
  "Aquisição",
  "Backoffice",
  "Consultoria",
  "Diretoria",
  "Finanças & Compliance",
  "Pessoas & Cultura",
  "Securitário",
  "Sucesso do Cliente",
  "Tecnologia",
  "+Novo"
];

// Outras opções:
const categoryOptions = ["Clientes", "Comissões e/ou SplitC", "Basement", "Outro"];
const priorityOptions = [
  "Baixa (7 dias úteis)",
  "Média (5 dias úteis)",
  "Alta (2 dias úteis)",
  "Urgente (1 dia útil)"
];
const statusOptions = ["Aberto", "Em andamento", "Fechado"];
const responsibleOptions = ["Nayla Martins", "Jonathan Kauer"];

// Função para gerar ID aleatório com 13 caracteres
function generateTicketId() {
  const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
  let result = "";
  for (let i = 0; i < 13; i++) {
    result += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return result;
}

function App() {
  // Estado para login
  const [currentUser, setCurrentUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loginUser, setLoginUser] = useState("");
  const [loginAdmin, setLoginAdmin] = useState(false);
  const [loginPassword, setLoginPassword] = useState("");

  // Estado do aplicativo de chamados
  const [tickets, setTickets] = useState([]);
  const [showNewTicketForm, setShowNewTicketForm] = useState(false);

  // Estados para campos do chamado (removendo Nome do Solicitante)
  const [cargoDepartamento, setCargoDepartamento] = useState('');
  const [emailSolicitante, setEmailSolicitante] = useState('');
  const [descricaoProblema, setDescricaoProblema] = useState('');
  const [categoria, setCategoria] = useState('');
  const [prioridade, setPrioridade] = useState('');
  const [newTicketFiles, setNewTicketFiles] = useState([]);

  // Estados para comentários
  const [newComment, setNewComment] = useState('');
  const [newCommentFiles, setNewCommentFiles] = useState([]);

  // Gerenciar opções de cargo (permitir "+Novo")
  const [cargoOptions, setCargoOptions] = useState(initialCargoOptions);

  // Estado para controle de visualização/resumo dos chamados
  const [expandedTickets, setExpandedTickets] = useState({});

  // Handler para alternar visualização do chamado (resumo/detalhado)
  const toggleTicketExpansion = (ticketId) => {
    setExpandedTickets(prev => ({ ...prev, [ticketId]: !prev[ticketId] }));
  };

  // Handler para seleção de cargo
  const handleCargoChange = (e) => {
    const value = e.target.value;
    if (value === "+Novo") {
      const novoCargo = prompt("Digite o novo cargo/departamento:");
      if (novoCargo) {
        setCargoOptions([...cargoOptions.slice(0, cargoOptions.length - 1), novoCargo, "+Novo"]);
        setCargoDepartamento(novoCargo);
      } else {
        setCargoDepartamento('');
      }
    } else {
      setCargoDepartamento(value);
    }
  };

  // Handler para anexos no novo chamado
  const handleTicketFileChange = (e) => {
    setNewTicketFiles(Array.from(e.target.files));
  };

  // Handler para anexos nos comentários
  const handleCommentFileChange = (e) => {
    setNewCommentFiles(Array.from(e.target.files));
  };

  // Criação de um novo chamado
  const handleCreateTicket = (e) => {
    e.preventDefault();
    const dataDeAbertura = new Date().toLocaleString();

    const newTicket = {
      id: generateTicketId(),
      nomeSolicitante: currentUser, // Pega o usuário logado
      cargoDepartamento,
      emailSolicitante,
      descricaoProblema,
      categoria,
      prioridade,
      dataDeAbertura,
      status: "Aberto",
      dataResolucao: '',
      responsavel: '',
      comentarios: [],
      attachments: newTicketFiles
    };

    setTickets(prev => [...prev, newTicket]);

    // Limpa os campos do formulário
    setCargoDepartamento('');
    setEmailSolicitante('');
    setDescricaoProblema('');
    setCategoria('');
    setPrioridade('');
    setNewTicketFiles([]);
    setShowNewTicketForm(false);
  };

  // Adiciona comentário a um chamado
  const handleAddComment = (ticketId) => {
    if (!newComment.trim()) return;
    const comment = {
      text: newComment,
      user: currentUser,
      timestamp: new Date().toLocaleString(),
      attachments: newCommentFiles
    };
    setTickets(prev =>
      prev.map(ticket =>
        ticket.id === ticketId ? { ...ticket, comentarios: [...ticket.comentarios, comment] } : ticket
      )
    );
    setNewComment('');
    setNewCommentFiles([]);
  };

  // Atualiza campos pelo admin
  const handleAdminUpdate = (ticketId, field, value) => {
    setTickets(prev =>
      prev.map(ticket =>
        ticket.id === ticketId ? { ...ticket, [field]: value } : ticket
      )
    );
  };

  // Filtra os chamados para exibição: admin vê todos; usuário só os dele
  const visibleTickets = tickets.filter(ticket => {
    if (isAdmin) return true;
    return ticket.nomeSolicitante === currentUser;
  });

  // Handler para o formulário de login
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    // Se for admin, verifica a senha
    if (loginAdmin && loginPassword !== "admin123") {
      alert("Senha incorreta para Admin!");
      return;
    }
    setCurrentUser(loginUser);
    setIsAdmin(loginAdmin);
    // Limpa os campos de login
    setLoginUser("");
    setLoginAdmin(false);
    setLoginPassword("");
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setIsAdmin(false);
  };

  // Função auxiliar para renderizar anexos como links para visualização/download
  const renderAttachments = (files) => {
    return (
      <ul className="list-disc list-inside">
        {files.map((file, idx) => {
          // Cria um URL temporário para o arquivo
          const fileURL = URL.createObjectURL(file);
          return (
            <li key={idx}>
              <a href={fileURL} download target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">
                {file.name}
              </a>
            </li>
          );
        })}
      </ul>
    );
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4">
      {/* Logo da empresa (coloque o arquivo logo.png na pasta public) */}
      <img src="/logo.png" alt="FinDesk Logo" className="h-12 mb-4" />
      <motion.h1
        className="text-3xl font-bold mb-4"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        FinDesk
      </motion.h1>

      {/* Tela de Login */}
      {!currentUser && (
        <form onSubmit={handleLoginSubmit} className="bg-white shadow p-4 rounded-2xl w-full max-w-md mb-4">
          <h2 className="text-xl font-bold mb-4">Faça seu login</h2>
          <div className="mb-2">
            <label className="block font-semibold">Nome de Usuário:</label>
            <input
              type="text"
              value={loginUser}
              onChange={(e) => setLoginUser(e.target.value)}
              required
              className="border rounded px-2 py-1 w-full"
            />
          </div>
          <div className="mb-2 flex items-center">
            <input
              type="checkbox"
              checked={loginAdmin}
              onChange={(e) => setLoginAdmin(e.target.checked)}
              className="mr-2"
            />
            <span>Entrar como Admin</span>
          </div>
          {loginAdmin && (
            <div className="mb-2">
              <label className="block font-semibold">Senha de Admin:</label>
              <input
                type="password"
                value={loginPassword}
                onChange={(e) => setLoginPassword(e.target.value)}
                required={loginAdmin}
                className="border rounded px-2 py-1 w-full"
              />
            </div>
          )}
          <button type="submit" className="px-3 py-1 bg-blue-500 text-white rounded shadow">
            Entrar
          </button>
        </form>
      )}

      {/* Conteúdo do App */}
      {currentUser && (
        <>
          <div className="flex items-center gap-2 mb-4">
            <p className="text-lg">
              Logado como: <span className="font-bold">{currentUser}</span> {isAdmin && <span className="text-red-500">(Admin)</span>}
            </p>
            <button onClick={handleLogout} className="px-3 py-1 bg-blue-500 text-white rounded shadow">
              Sair
            </button>
            {!isAdmin && (
              <button onClick={() => setShowNewTicketForm(true)} className="px-3 py-1 bg-green-500 text-white rounded shadow">
                Criar Novo Chamado
              </button>
            )}
          </div>

          {/* Lista de Chamados */}
          <div className="grid gap-4 w-full max-w-5xl">
            {visibleTickets.map((ticket) => (
              <motion.div
                key={ticket.id}
                className="bg-white shadow p-4 rounded-2xl"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
              >
                {/* Janela Resumo com toggle para expandir */}
                <div className="flex justify-between items-center cursor-pointer" onClick={() => toggleTicketExpansion(ticket.id)}>
                  <div>
                    <h2 className="text-xl font-bold">ID: {ticket.id}</h2>
                    <p className="text-gray-700">
                      <span className="font-semibold">Categoria:</span> {ticket.categoria} | <span className="font-semibold">Prioridade:</span> {ticket.prioridade}
                    </p>
                    <p className="text-gray-700">
                      <span className="font-semibold">Data:</span> {ticket.dataDeAbertura}
                    </p>
                  </div>
                  <button className="px-2 py-1 bg-gray-300 rounded">
                    {expandedTickets[ticket.id] ? "Ocultar" : "Ver Detalhes"}
                  </button>
                </div>

                {expandedTickets[ticket.id] && (
                  <div className="mt-4">
                    <p className="text-gray-700 mb-1">
                      <span className="font-semibold">Nome do Solicitante:</span> {ticket.nomeSolicitante}
                    </p>
                    <p className="text-gray-700 mb-1">
                      <span className="font-semibold">Cargo/Departamento:</span> {ticket.cargoDepartamento}
                    </p>
                    <p className="text-gray-700 mb-1">
                      <span className="font-semibold">E-mail:</span> {ticket.emailSolicitante}
                    </p>
                    <p className="text-gray-700 mb-1 whitespace-pre-wrap">
                      <span className="font-semibold">Descrição:</span> {ticket.descricaoProblema}
                    </p>
                    <div className="mb-2">
                      <label className="font-semibold">Status:</label>{" "}
                      {isAdmin ? (
                        <select
                          value={ticket.status}
                          onChange={(e) => handleAdminUpdate(ticket.id, 'status', e.target.value)}
                          className="border rounded px-2 py-1 ml-1"
                        >
                          {statusOptions.map((option, idx) => (
                            <option key={idx} value={option}>{option}</option>
                          ))}
                        </select>
                      ) : (
                        <span className="ml-1">{ticket.status}</span>
                      )}
                    </div>
                    <div className="mb-2">
                      <label className="font-semibold">Data de Resolução:</label>{" "}
                      {isAdmin ? (
                        <input
                          type="text"
                          placeholder="dd/mm/aaaa ou deixe em branco"
                          value={ticket.dataResolucao}
                          onChange={(e) => handleAdminUpdate(ticket.id, 'dataResolucao', e.target.value)}
                          className="border rounded px-2 py-1 ml-1"
                        />
                      ) : (
                        <span className="ml-1">{ticket.dataResolucao}</span>
                      )}
                    </div>
                    <div className="mb-2">
                      <label className="font-semibold">Responsável:</label>{" "}
                      {isAdmin ? (
                        <select
                          value={ticket.responsavel}
                          onChange={(e) => handleAdminUpdate(ticket.id, 'responsavel', e.target.value)}
                          className="border rounded px-2 py-1 ml-1"
                        >
                          <option value="">Selecione</option>
                          {responsibleOptions.map((option, idx) => (
                            <option key={idx} value={option}>{option}</option>
                          ))}
                        </select>
                      ) : (
                        <span className="ml-1">{ticket.responsavel}</span>
                      )}
                    </div>
                    <div className="mb-2">
                      <label className="font-semibold">Anexos:</label>
                      {ticket.attachments && ticket.attachments.length > 0 ? (
                        renderAttachments(ticket.attachments)
                      ) : (
                        <span> Nenhum anexo</span>
                      )}
                    </div>
                    <hr />
                    <div className="mb-2">
                      <p className="text-gray-700 font-semibold">Comentários/Atualizações:</p>
                      {ticket.comentarios.map((cmt, idx) => (
                        <div key={idx} className="bg-gray-100 p-2 rounded-lg mb-2">
                          <p className="text-sm whitespace-pre-wrap">
                            <span className="font-semibold">{cmt.user}:</span> {cmt.text}
                          </p>
                          {cmt.attachments && cmt.attachments.length > 0 && (
                            <ul className="list-disc list-inside">
                              {cmt.attachments.map((file, i) => {
                                const fileURL = URL.createObjectURL(file);
                                return (
                                  <li key={i}>
                                    <a href={fileURL} download target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">
                                      {file.name}
                                    </a>
                                  </li>
                                );
                              })}
                            </ul>
                          )}
                          <p className="text-xs text-gray-500">{cmt.timestamp}</p>
                        </div>
                      ))}
                    </div>
                    {currentUser && (
                      <div className="mb-4">
                        <textarea
                          value={newComment}
                          onChange={(e) => setNewComment(e.target.value)}
                          placeholder="Digite um comentário ou atualização..."
                          className="w-full border rounded-lg p-2 mb-2 whitespace-pre-wrap"
                          rows="3"
                        />
                        <input
                          type="file"
                          multiple
                          onChange={handleCommentFileChange}
                          className="w-full mb-2"
                        />
                        <button
                          onClick={() => handleAddComment(ticket.id)}
                          className="px-3 py-1 bg-blue-500 text-white rounded-lg shadow"
                        >
                          Enviar Comentário
                        </button>
                      </div>
                    )}
                  </div>
                )}
              </motion.div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
