import React, { useState } from "react";
import { motion } from "framer-motion";

// Configuração visual
const primaryTextColor = "#0E1428";
const overdueBackgroundColor = "#FFCCCC";
const buttonColor = "#FF5E00";

// Opções fixas
const initialCargoOptions = [
  "Aquisição", "Backoffice", "Consultoria", "Diretoria", "Finanças & Compliance",
  "Pessoas & Cultura", "Securitário", "Sucesso do Cliente", "Tecnologia", "+Novo"
];

const categoryOptions = ["Clientes", "Comissões e/ou SplitC", "Basement", "Novo+"];
const priorityOptions = [
  { label: "Baixa (7 dias úteis)", days: 7 },
  { label: "Média (5 dias úteis)", days: 5 },
  { label: "Alta (2 dias úteis)", days: 2 },
  { label: "Urgente (1 dia útil)", days: 1 }
];
const statusOptions = ["Aberto", "Em andamento", "Fechado"];
const responsibleOptions = ["Nayla Martins", "Jonathan Kauer"];

// Gera um ID aleatório
const generateTicketId = () => {
  const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
  return Array.from({ length: 13 }, () => chars[Math.floor(Math.random() * chars.length)]).join("");
};

// Calcula a data de vencimento com base na prioridade
const calculateDueDate = (days) => {
  let date = new Date();
  let count = 0;
  while (count < days) {
    date.setDate(date.getDate() + 1);
    if (date.getDay() !== 0 && date.getDay() !== 6) {
      count++;
    }
  }
  return date.toLocaleDateString();
};

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loginUser, setLoginUser] = useState("");
  const [loginAdmin, setLoginAdmin] = useState(false);
  const [loginPassword, setLoginPassword] = useState("");

  const [tickets, setTickets] = useState([]);
  const [showNewTicketForm, setShowNewTicketForm] = useState(false);
  const [activeTab, setActiveTab] = useState("abertos");

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    if (loginAdmin && loginPassword !== "admin123@guiainvestgpt") {
      alert("Senha incorreta para Admin!");
      return;
    }
    setCurrentUser(loginUser);
    setIsAdmin(loginAdmin);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <img src="/logo.png" alt="FinDesk Logo" className="h-12 mb-4" />
      <motion.h1 className="text-3xl font-bold mb-4" style={{ color: primaryTextColor }}>
        FinDesk
      </motion.h1>

      {!currentUser && (
        <form onSubmit={handleLoginSubmit} className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md text-center">
          <h2 className="text-xl font-bold mb-4">Faça seu login</h2>
          <input type="text" placeholder="Nome de Usuário" value={loginUser} onChange={(e) => setLoginUser(e.target.value)} required className="border rounded p-2 w-full mb-2" />
          <div className="mb-2">
            <input type="checkbox" checked={loginAdmin} onChange={(e) => setLoginAdmin(e.target.checked)} className="mr-2" />
            <span>Entrar como Admin</span>
          </div>
          {loginAdmin && (
            <input type="password" placeholder="Senha" value={loginPassword} onChange={(e) => setLoginPassword(e.target.value)} required className="border rounded p-2 w-full mt-2" />
          )}
          <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded mt-2">Entrar</button>
        </form>
      )}
    </div>
  );
}

export default App;
