import React, { useState } from 'react';
import { motion } from 'framer-motion';
import favicon from '/logo.png';

// Opções para Cargo/Departamento
const initialCargoOptions = [
  "Aquisição", "Backoffice", "Consultoria", "Diretoria", "Finanças & Compliance", "Pessoas & Cultura", "Securitário", "Sucesso do Cliente", "Tecnologia", "+Novo"
];

// Outras opções
const categoryOptions = ["Clientes", "Comissões e/ou SplitC", "Basement", "Novo+"];
const priorityOptions = [
  { label: "Baixa", days: 7 },
  { label: "Média", days: 5 },
  { label: "Alta", days: 2 },
  { label: "Urgente", days: 1 }
];
const statusOptions = ["Aberto", "Em andamento", "Fechado"];
const responsibleOptions = ["Nayla Martins", "Jonathan Kauer"];

// Função para gerar ID aleatório
function generateTicketId() {
  return Math.random().toString(36).substr(2, 13).toUpperCase();
}

// Calcula a data de vencimento
function calculateDueDate(priority) {
  const days = priorityOptions.find(p => p.label === priority)?.days || 7;
  let date = new Date();
  for (let i = 0; i < days; i++) {
    date.setDate(date.getDate() + 1);
    if (date.getDay() === 6) date.setDate(date.getDate() + 2); // Evita sábado
    if (date.getDay() === 0) date.setDate(date.getDate() + 1); // Evita domingo
  }
  return date.toLocaleDateString();
}

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loginUser, setLoginUser] = useState("");
  const [loginAdmin, setLoginAdmin] = useState(false);
  const [loginPassword, setLoginPassword] = useState("");
  const [tickets, setTickets] = useState([]);
  const [showNewTicketForm, setShowNewTicketForm] = useState(false);
  const [tab, setTab] = useState("abertos");

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    if (loginAdmin && loginPassword !== "admin123@guiainvestgpt") {
      alert("Senha incorreta para Admin!");
      return;
    }
    setCurrentUser(loginUser);
    setIsAdmin(loginAdmin);
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setIsAdmin(false);
  };

  const handleCreateTicket = (e) => {
    e.preventDefault();
    const priority = e.target.priority.value;
    const newTicket = {
      id: generateTicketId(),
      nomeSolicitante: currentUser,
      cargoDepartamento: e.target.cargo.value,
      emailSolicitante: e.target.email.value,
      descricaoProblema: e.target.descricao.value,
      categoria: e.target.categoria.value === "Novo+" ? prompt("Informe a nova categoria:") : e.target.categoria.value,
      prioridade: priority,
      dataDeAbertura: new Date().toLocaleDateString(),
      prazoFinal: calculateDueDate(priority),
      status: "Aberto",
      dataResolucao: "",
      responsavel: "",
    };
    setTickets([...tickets, newTicket]);
    setShowNewTicketForm(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4 text-[#0E1428]">
      <link rel="icon" href={favicon} />
      <title>FinDesk</title>
      <img src="/logo.png" alt="FinDesk Logo" className="h-12 mb-4" />
      <motion.h1 className="text-3xl font-bold mb-4">FinDesk</motion.h1>

      {!currentUser && (
        <form onSubmit={handleLoginSubmit} className="bg-white shadow p-4 rounded-2xl w-full max-w-md">
          <h2 className="text-xl font-bold mb-4">Faça seu login</h2>
          <input type="text" placeholder="Nome de Usuário" value={loginUser} onChange={(e) => setLoginUser(e.target.value)} required className="border rounded px-2 py-1 w-full mb-2" />
          <label className="flex items-center mb-2">
            <input type="checkbox" checked={loginAdmin} onChange={(e) => setLoginAdmin(e.target.checked)} className="mr-2" /> Entrar como Admin
          </label>
          {loginAdmin && <input type="password" placeholder="Senha" value={loginPassword} onChange={(e) => setLoginPassword(e.target.value)} required className="border rounded px-2 py-1 w-full mb-2" />}
          <button type="submit" className="px-3 py-1 bg-blue-500 text-white rounded">Entrar</button>
        </form>
      )}

      {currentUser && (
        <>
          <div className="flex justify-between w-full max-w-3xl mb-4">
            <p>Logado como: <strong>{currentUser}</strong> {isAdmin && <span className="text-red-500">(Admin)</span>}</p>
            <button onClick={handleLogout} className="px-3 py-1 bg-blue-500 text-white rounded">Sair</button>
          </div>
          <div className="flex gap-2 mb-4">
            <button className={`px-3 py-1 rounded ${tab === "abertos" ? "bg-gray-300" : "bg-gray-200"}`} onClick={() => setTab("abertos")}>Chamados Abertos</button>
            <button className={`px-3 py-1 rounded ${tab === "concluidos" ? "bg-gray-300" : "bg-gray-200"}`} onClick={() => setTab("concluidos")}>Chamados Concluídos</button>
          </div>
          {!isAdmin && <button onClick={() => setShowNewTicketForm(true)} className="px-3 py-1 bg-[#FF5E00] text-white rounded">Criar Novo Chamado</button>}
          {showNewTicketForm && !isAdmin && <form onSubmit={handleCreateTicket} className="bg-white shadow p-4 rounded-2xl w-full max-w-lg mt-4">
            <h2 className="text-xl font-bold mb-4">Novo Chamado</h2>
            <input type="email" name="email" placeholder="E-mail do Solicitante" required className="border rounded px-2 py-1 w-full mb-2" />
            <input type="text" name="descricao" placeholder="Descrição" required className="border rounded px-2 py-1 w-full mb-2" />
            <button type="submit" className="px-3 py-1 bg-blue-500 text-white rounded">Criar Chamado</button>
          </form>}
        </>
      )}
    </div>
  );
}

export default App;
